import React, { useState, useCallback, useRef } from 'react';
import ReactFlow, {
  ReactFlowProvider,
  addEdge,
  useNodesState,
  useEdgesState,
  Controls,
  Background,
  MarkerType,
  Handle,
  Position,
} from 'reactflow';
import 'reactflow/dist/style.css';
import './App.css';

// Custom Node Components
const StartNode = () => {
  return (
    <div className="start-node node">
      <div className="node-content">
        <div className="node-icon">ðŸ“Œ</div>
        <div>Start</div>
      </div>
      <Handle type="source" position={Position.Bottom} id="start-out" />
    </div>
  );
};

const EndNode = () => {
  return (
    <div className="end-node node">
      <Handle type="target" position={Position.Top} id="end-in" />
      <div className="node-content">
        <div>END</div>
      </div>
    </div>
  );
};

const ActionNode = ({ data, id }) => {
  const handleNameChange = (evt) => {
    data.onChange(id, evt.target.value);
  };

  return (
    <div className="action-node node">
      <Handle type="target" position={Position.Top} />
      <div className="node-content">
        <div className="node-icon">ðŸ”§</div>
        <input
          type="text"
          value={data.name}
          onChange={handleNameChange}
          className="node-input"
          placeholder="Action Name"
        />
        <button className="delete-button" onClick={() => data.onDelete(id)}>
          Delete
        </button>
      </div>
      <Handle type="source" position={Position.Bottom} />
    </div>
  );
};

const IfElseNode = ({ data, id }) => {
  const [showForm, setShowForm] = useState(false);
  const [newBranchName, setNewBranchName] = useState('');

  const toggleForm = () => {
    setShowForm(!showForm);
    setNewBranchName('');
  };

  const handleNameChange = (evt) => {
    data.onChange(id, evt.target.value);
  };

  const handleAddBranch = () => {
    if (newBranchName.trim()) {
      data.onAddBranch(id, newBranchName);
      setNewBranchName('');
    }
  };

  const handleBranchNameChange = (branchId, newName) => {
    data.onChangeBranchName(id, branchId, newName);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleAddBranch();
    }
  };

  return (
    <div className="if-else-node node">
      <Handle type="target" position={Position.Top} />
      <div className="node-content">
        <div className="node-header">
          <div className="node-icon">ðŸ”€</div>
          <input
            type="text"
            value={data.name}
            onChange={handleNameChange}
            className="node-input"
            placeholder="If/Else Condition"
          />
        </div>

        <div className="node-actions">
          <button className="edit-button" onClick={toggleForm}>
            {showForm ? 'Close' : 'Edit Branches'}
          </button>
          <button className="delete-button" onClick={() => data.onDelete(id)}>
            Delete
          </button>
        </div>

        {showForm && (
          <div className="branch-form">
            <div className="branch-list">
              <h4>Branches:</h4>
              {data.branches.map((branch) => (
                <div key={branch.id} className="branch-item">
                  <input
                    type="text"
                    value={branch.name}
                    onChange={(e) => handleBranchNameChange(branch.id, e.target.value)}
                    placeholder="Branch Name"
                    className="branch-input"
                  />
                </div>
              ))}
              <div className="add-branch">
                <input
                  type="text"
                  value={newBranchName}
                  onChange={(e) => setNewBranchName(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="New Branch Name"
                  className="branch-input"
                />
                <button onClick={handleAddBranch} className="add-branch-button">Add Branch</button>
              </div>
            </div>
          </div>
        )}
      </div>
      <Handle type="source" position={Position.Bottom} id="if-source" />
    </div>
  );
};

const BranchNode = ({ data }) => {
  return (
    <div className="branch-node node">
      <Handle type="target" position={Position.Top} />
      <div className="node-content">
        <div className="branch-label">{data.name}</div>
      </div>
      <Handle type="source" position={Position.Bottom} />
    </div>
  );
};

const ElseNode = () => {
  return (
    <div className="else-node node">
      <Handle type="target" position={Position.Top} />
      <div className="node-content">
        <div className="branch-label">Else</div>
      </div>
      <Handle type="source" position={Position.Bottom} />
    </div>
  );
};

const NodeSelection = ({ position, onSelect, onClose }) => {
  return (
    <div 
      className="node-selection-menu" 
      style={{ 
        position: 'absolute', 
        left: position.x, 
        top: position.y 
      }}
    >
      <div className="menu-title">Add Node:</div>
      <button className="menu-item" onClick={() => onSelect('action')}>
        Action Node
      </button>
      <button className="menu-item" onClick={() => onSelect('ifElse')}>
        If/Else Node
      </button>
      <button className="menu-close" onClick={onClose}>
        Cancel
      </button>
    </div>
  );
};

const AddButton = ({ onClick, edgeId }) => {
  return (
    <div className="add-button-container">
      <button className="add-button" onClick={(e) => onClick(e, edgeId)}>
        +
      </button>
    </div>
  );
};

// Define custom node types
const nodeTypes = {
  start: StartNode,
  end: EndNode,
  action: ActionNode,
  ifElse: IfElseNode,
  branch: BranchNode,
  else: ElseNode,
};

// Initial workflow with Start and End nodes
const initialNodes = [
  {
    id: 'start',
    type: 'start',
    position: { x: 250, y: 5 },
    data: {},
    draggable: false,
  },
  {
    id: 'end',
    type: 'end',
    position: { x: 250, y: 200 },
    data: {},
    draggable: false,
  },
];

// Initial connection between Start and End nodes
const initialEdges = [
  {
    id: 'start-end',
    source: 'start',
    target: 'end',
    type: 'smoothstep',
    markerEnd: {
      type: MarkerType.ArrowClosed,
    },
  },
];

// Custom edge with Add button
const ButtonEdge = ({ id, sourceX, sourceY, targetX, targetY, onClick }) => {
  const edgePath = `M${sourceX},${sourceY} C${sourceX},${(sourceY + targetY) / 2} ${targetX},${(sourceY + targetY) / 2} ${targetX},${targetY}`;
  const middleX = (sourceX + targetX) / 2;
  const middleY = (sourceY + targetY) / 2;

  return (
    <>
      <path
        id={id}
        className="react-flow__edge-path"
        d={edgePath}
        markerEnd="url(#arrowclosed)"
      />
      <foreignObject
        width={40}
        height={40}
        x={middleX - 20}
        y={middleY - 20}
        className="edgebutton-foreignobject"
      >
        <AddButton onClick={(e) => onClick(e, id)} edgeId={id} />
      </foreignObject>
    </>
  );
};

const edgeTypes = {
  buttonedge: ButtonEdge,
};

function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);

  const [showNodeMenu, setShowNodeMenu] = useState(false);
  const [menuPosition, setMenuPosition] = useState({ x: 0, y: 0 });
  const [selectedEdgeId, setSelectedEdgeId] = useState(null);

  const nodeIdCounter = useRef(1);
  const branchIdCounter = useRef(1);

  const generateNodeId = (prefix) => `${prefix}-${nodeIdCounter.current++}`;
  const generateBranchId = () => `branch-${branchIdCounter.current++}`;

  // Handle connecting nodes
  const onConnect = useCallback(
    (params) => {
      setEdges((eds) =>
        addEdge(
          {
            ...params,
            type: 'buttonedge',
            markerEnd: { type: MarkerType.ArrowClosed },
            data: { onClick: handleAddButtonClick },
          },
          eds
        )
      );
    },
    [setEdges]
  );

  // Handle edge click to show Add button
  const handleAddButtonClick = useCallback((event, edgeId) => {
    event.stopPropagation();
    setSelectedEdgeId(edgeId);
    setShowNodeMenu(true);
    setMenuPosition({ 
      x: event.clientX, 
      y: event.clientY 
    });
  }, []);

  // Handle background click to hide node menu
  const onPaneClick = useCallback(() => {
    setShowNodeMenu(false);
    setSelectedEdgeId(null);
  }, []);

  // Calculate position for the new node
  const calculateNewNodePosition = useCallback(
    (edgeId) => {
      const edge = edges.find((e) => e.id === edgeId);
      if (!edge) return { x: 0, y: 0 };

      // Find source and target nodes
      const sourceNode = nodes.find((n) => n.id === edge.source);
      const targetNode = nodes.find((n) => n.id === edge.target);

      // Calculate middle position
      const x = (sourceNode.position.x + targetNode.position.x) / 2;
      const y = (sourceNode.position.y + targetNode.position.y) / 2;

      return { x, y };
    },
    [edges, nodes]
  );

  // Add Action Node
  const addActionNode = useCallback(() => {
    if (!selectedEdgeId) return;

    const edge = edges.find((e) => e.id === selectedEdgeId);
    if (!edge) return;

    const pos = calculateNewNodePosition(selectedEdgeId);
    const newNodeId = generateNodeId('action');

    // Create new action node
    const newNode = {
      id: newNodeId,
      type: 'action',
      position: pos,
      data: {
        name: 'New Action',
        onChange: handleChangeNodeName,
        onDelete: handleDeleteNode,
      },
    };

    // Create new edges
    const newEdge1 = {
      id: `${edge.source}-${newNodeId}`,
      source: edge.source,
      target: newNodeId,
      type: 'buttonedge',
      markerEnd: { type: MarkerType.ArrowClosed },
      data: { onClick: handleAddButtonClick },
    };

    const newEdge2 = {
      id: `${newNodeId}-${edge.target}`,
      source: newNodeId,
      target: edge.target,
      type: 'buttonedge',
      markerEnd: { type: MarkerType.ArrowClosed },
      data: { onClick: handleAddButtonClick },
    };

    // Update nodes and edges
    setNodes((nds) => [...nds, newNode]);
    setEdges((eds) => {
      const filteredEdges = eds.filter((e) => e.id !== selectedEdgeId);
      return [...filteredEdges, newEdge1, newEdge2];
    });

    setShowNodeMenu(false);
    setSelectedEdgeId(null);
  }, [selectedEdgeId, edges, calculateNewNodePosition, setNodes, setEdges, handleAddButtonClick]);

  // Add If/Else Node
  const addIfElseNode = useCallback(() => {
    if (!selectedEdgeId) return;

    const edge = edges.find((e) => e.id === selectedEdgeId);
    if (!edge) return;

    const pos = calculateNewNodePosition(selectedEdgeId);
    const ifElseId = generateNodeId('ifElse');
    const branchId = generateBranchId();
    const branchNodeId = generateNodeId('branch');
    const elseNodeId = generateNodeId('else');

    // Create If/Else node
    const ifElseNode = {
      id: ifElseId,
      type: 'ifElse',
      position: pos,
      data: {
        name: 'Condition',
        branches: [{ id: branchId, name: 'True' }],
        onChange: handleChangeNodeName,
        onDelete: handleDeleteIfElseNode,
        onAddBranch: handleAddBranch,
        onChangeBranchName: handleChangeBranchName,
      },
    };

    // Create branch node
    const branchNode = {
      id: branchNodeId,
      type: 'branch',
      position: { x: pos.x - 100, y: pos.y + 100 },
      data: { name: 'True' },
      parentNode: ifElseId,
    };

    // Create else node
    const elseNode = {
      id: elseNodeId,
      type: 'else',
      position: { x: pos.x + 100, y: pos.y + 100 },
      data: {},
      parentNode: ifElseId,
    };

    // Create new edges
    const newEdge1 = {
      id: `${edge.source}-${ifElseId}`,
      source: edge.source,
      target: ifElseId,
      type: 'buttonedge',
      markerEnd: { type: MarkerType.ArrowClosed },
      data: { onClick: handleAddButtonClick },
    };

    const newEdge2 = {
      id: `${branchNodeId}-${edge.target}`,
      source: branchNodeId,
      target: edge.target,
      type: 'buttonedge',
      markerEnd: { type: MarkerType.ArrowClosed },
      data: { onClick: handleAddButtonClick },
    };

    const newEdge3 = {
      id: `${elseNodeId}-${edge.target}`,
      source: elseNodeId,
      target: edge.target,
      type: 'buttonedge',
      markerEnd: { type: MarkerType.ArrowClosed },
      data: { onClick: handleAddButtonClick },
    };

    // Update nodes and edges
    setNodes((nds) => [...nds, ifElseNode, branchNode, elseNode]);
    setEdges((eds) => {
      const filteredEdges = eds.filter((e) => e.id !== selectedEdgeId);
      return [...filteredEdges, newEdge1, newEdge2, newEdge3];
    });

    setShowNodeMenu(false);
    setSelectedEdgeId(null);
  }, [selectedEdgeId, edges, calculateNewNodePosition, setNodes, setEdges, handleAddButtonClick]);

  // Change node name
  const handleChangeNodeName = useCallback(
    (nodeId, newName) => {
      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === nodeId) {
            return {
              ...node,
              data: {
                ...node.data,
                name: newName,
              },
            };
          }
          return node;
        })
      );
    },
    [setNodes]
  );

  // Delete Action node
  const handleDeleteNode = useCallback(
    (nodeId) => {
      // Find incoming and outgoing edges
      const incomingEdges = edges.filter((e) => e.target === nodeId);
      const outgoingEdges = edges.filter((e) => e.source === nodeId);

      // Create new connections between the nodes that were connected to this node
      const newConnections = [];
      for (const incoming of incomingEdges) {
        for (const outgoing of outgoingEdges) {
          newConnections.push({
            id: `${incoming.source}-${outgoing.target}`,
            source: incoming.source,
            target: outgoing.target,
            type: 'buttonedge',
            markerEnd: { type: MarkerType.ArrowClosed },
            data: { onClick: handleAddButtonClick },
          });
        }
      }

      // Remove the node and its connected edges
      setNodes((nds) => nds.filter((node) => node.id !== nodeId));
      setEdges((eds) => {
        const filteredEdges = eds.filter(
          (e) => e.source !== nodeId && e.target !== nodeId
        );
        return [...filteredEdges, ...newConnections];
      });
    },
    [edges, setNodes, setEdges, handleAddButtonClick]
  );

  // Delete If/Else node
  const handleDeleteIfElseNode = useCallback(
    (nodeId) => {
      // Find all child nodes
      const childNodes = nodes.filter((node) => node.parentNode === nodeId);
      const childNodeIds = childNodes.map((node) => node.id);

      // Find incoming and outgoing edges
      const incomingEdges = edges.filter((e) => e.target === nodeId);
      const outgoingEdges = edges.filter((e) => 
        childNodeIds.includes(e.source) && !childNodeIds.includes(e.target)
      );

      // Create new connections
      const newConnections = [];
      for (const incoming of incomingEdges) {
        for (const outgoing of outgoingEdges) {
          newConnections.push({
            id: `${incoming.source}-${outgoing.target}`,
            source: incoming.source,
            target: outgoing.target,
            type: 'buttonedge',
            markerEnd: { type: MarkerType.ArrowClosed },
            data: { onClick: handleAddButtonClick },
          });
        }
      }

      // Remove the If/Else node, its children, and connected edges
      setNodes((nds) => nds.filter((node) => node.id !== nodeId && node.parentNode !== nodeId));
      setEdges((eds) => {
        const filteredEdges = eds.filter(
          (e) => 
            e.source !== nodeId && 
            e.target !== nodeId && 
            !childNodeIds.includes(e.source) && 
            !childNodeIds.includes(e.target)
        );
        return [...filteredEdges, ...newConnections];
      });
    },
    [nodes, edges, setNodes, setEdges, handleAddButtonClick]
  );

  // Add branch to If/Else node
  const handleAddBranch = useCallback(
    (nodeId, branchName) => {
      const ifElseNode = nodes.find((node) => node.id === nodeId);
      if (!ifElseNode) return;

      const branchId = generateBranchId();
      const branchNodeId = generateNodeId('branch');

      // Update If/Else node with new branch
      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === nodeId) {
            const updatedBranches = [
              ...node.data.branches,
              { id: branchId, name: branchName }
            ];

            return {
              ...node,
              data: {
                ...node.data,
                branches: updatedBranches,
              },
            };
          }
          return node;
        })
      );

      // Calculate position for new branch node
      const existingBranchNodes = nodes.filter(
        (n) => n.parentNode === nodeId && n.type === 'branch'
      );
      const branchCount = existingBranchNodes.length;

      // Create new branch node
      const newBranchNode = {
        id: branchNodeId,
        type: 'branch',
        position: { 
          x: ifElseNode.position.x - 100 + branchCount * 100, 
          y: ifElseNode.position.y + 100 
        },
        data: { name: branchName },
        parentNode: nodeId,
      };

      // Find target node for new connection
      const elseNode = nodes.find((n) => n.parentNode === nodeId && n.type === 'else');
      const outgoingEdges = edges.filter((e) => e.source === elseNode.id);
      const targetNodeId = outgoingEdges[0]?.target;

      // Create new edge
      const newEdge = {
        id: `${branchNodeId}-${targetNodeId}`,
        source: branchNodeId,
        target: targetNodeId,
        type: 'buttonedge',
        markerEnd: { type: MarkerType.ArrowClosed },
        data: { onClick: handleAddButtonClick },
      };

      // Update nodes and edges
      setNodes((nds) => [...nds, newBranchNode]);
      setEdges((eds) => [...eds, newEdge]);
    },
    [nodes, edges, setNodes, setEdges, handleAddButtonClick]
  );

  // Change branch name
  const handleChangeBranchName = useCallback(
    (nodeId, branchId, newName) => {
      // Update the branch name in the If/Else node data
      setNodes((nds) =>
        nds.map((node) => {
          if (node.id === nodeId) {
            const updatedBranches = node.data.branches.map((branch) =>
              branch.id === branchId ? { ...branch, name: newName } : branch
            );

            return {
              ...node,
              data: {
                ...node.data,
                branches: updatedBranches,
              },
            };
          }
          return node;
        })
      );

      // Update the branch node label
      const branchIndex = nodes
        .find((n) => n.id === nodeId)
        ?.data.branches.findIndex((b) => b.id === branchId);

      if (branchIndex === undefined) return;

      const branchNodes = nodes.filter(
        (n) => n.parentNode === nodeId && n.type === 'branch'
      );

      if (branchNodes[branchIndex]) {
        setNodes((nds) =>
          nds.map((node) => {
            if (node.id === branchNodes[branchIndex].id) {
              return {
                ...node,
                data: {
                  ...node.data,
                  name: newName,
                },
              };
            }
            return node;
          })
        );
      }
    },
    [nodes, setNodes]
  );

  // Define custom components for ReactFlow
  const edgeOptions = {
    type: 'buttonedge',
    markerEnd: {
      type: MarkerType.ArrowClosed,
    },
    data: { onClick: handleAddButtonClick },
  };

  return (
    <div className="app">
      <h1>React Flow Workflow Builder</h1>
      <div className="flow-container">
        <ReactFlowProvider>
          <ReactFlow
            nodes={nodes}
            edges={edges}
            onNodesChange={onNodesChange}
            onEdgesChange={onEdgesChange}
            onConnect={onConnect}
            onPaneClick={onPaneClick}
            nodeTypes={nodeTypes}
            edgeTypes={edgeTypes}
            defaultEdgeOptions={edgeOptions}
            fitView
          >
            <Controls />
            <Background color="#aaa" gap={16} />

            {showNodeMenu && (
              <NodeSelection
                position={menuPosition}
                onSelect={(type) => {
                  if (type === 'action') {
                    addActionNode();
                  } else if (type === 'ifElse') {
                    addIfElseNode();
                  }
                }}
                onClose={() => {
                  setShowNodeMenu(false);
                  setSelectedEdgeId(null);
                }}
              />
            )}
          </ReactFlow>
        </ReactFlowProvider>
      </div>
    </div>
  );
}

export default function AppWithProvider() {
  return (
    <ReactFlowProvider>
      <App />
    </ReactFlowProvider>
  );
}
