import React, { useState, useCallback, useRef } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  addEdge,
  Panel,
} from 'reactflow';
import 'reactflow/dist/style.css';

// Custom node types
const nodeTypes = {
  start: ({ data, id }) => (
    <div style={{
      padding: '15px',
      borderRadius: '8px',
      background: '#e6f7ff',
      border: '1px solid #1890ff',
      width: '180px',
    }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
        <div style={{ 
          width: '30px', 
          height: '30px', 
          borderRadius: '50%', 
          background: '#e6f7ff', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          marginRight: '10px',
          border: '1px solid #1890ff'
        }}>
          <span role="img" aria-label="start">üöÄ</span>
        </div>
        <div style={{ fontWeight: 'bold' }}>Start Node</div>
      </div>
      <div>{data.label || 'Start'}</div>
    </div>
  ),
  action: ({ data, id }) => (
    <div style={{
      padding: '15px',
      borderRadius: '8px',
      background: '#f0f5ff',
      border: '1px solid #597ef7',
      width: '180px',
    }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
        <div style={{ 
          width: '30px', 
          height: '30px', 
          borderRadius: '50%', 
          background: '#f0f5ff', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          marginRight: '10px',
          border: '1px solid #597ef7'
        }}>
          <span role="img" aria-label="action">‚öôÔ∏è</span>
        </div>
        <div style={{ fontWeight: 'bold' }}>Action Node</div>
      </div>
      <div>{data.label || 'Action'}</div>
    </div>
  ),
  ifelse: ({ data, id }) => (
    <div style={{
      padding: '15px',
      borderRadius: '8px',
      background: '#fff7e6',
      border: '1px solid #ffc53d',
      width: '180px',
    }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
        <div style={{ 
          width: '30px', 
          height: '30px', 
          borderRadius: '50%', 
          background: '#fff7e6', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          marginRight: '10px',
          border: '1px solid #ffc53d'
        }}>
          <span role="img" aria-label="if-else">üîÄ</span>
        </div>
        <div style={{ fontWeight: 'bold' }}>If / Else</div>
      </div>
      <div>{data.label || 'If / Else'}</div>
    </div>
  ),
  end: ({ data, id }) => (
    <div style={{
      padding: '15px',
      borderRadius: '8px',
      background: '#f9f0ff',
      border: '1px solid #9254de',
      width: '180px',
    }}>
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
        <div style={{ 
          width: '30px', 
          height: '30px', 
          borderRadius: '50%', 
          background: '#f9f0ff', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          marginRight: '10px',
          border: '1px solid #9254de'
        }}>
          <span role="img" aria-label="end">üèÅ</span>
        </div>
        <div style={{ fontWeight: 'bold' }}>End Node</div>
      </div>
      <div>{data.label || 'END'}</div>
    </div>
  ),
  branch: ({ data, id }) => (
    <div style={{
      padding: '10px',
      borderRadius: '8px',
      background: '#f5f5f5',
      border: '1px solid #d9d9d9',
      width: '120px',
      textAlign: 'center',
    }}>
      <div>{data.label || 'Branch'}</div>
    </div>
  ),
};

const initialNodes = [
  {
    id: 'start',
    type: 'start',
    position: { x: 300, y: 50 },
    data: { label: 'Start' },
  },
  {
    id: 'end',
    type: 'end',
    position: { x: 300, y: 250 },
    data: { label: 'END' },
  },
];

const initialEdges = [
  { id: 'start->end', source: 'start', target: 'end', type: 'smoothstep' },
];

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [nodeCount, setNodeCount] = useState(1); // For unique node IDs
  const [selectedNode, setSelectedNode] = useState(null);
  const [showNodeMenu, setShowNodeMenu] = useState(false);
  const [menuPosition, setMenuPosition] = useState({ x: 0, y: 0 });
  const [clickedEdgeId, setClickedEdgeId] = useState(null);
  const [showNodeForm, setShowNodeForm] = useState(false);
  const [nodeFormData, setNodeFormData] = useState({ name: '', type: '' });
  const [branchCount, setBranchCount] = useState(1);
  const reactFlowWrapper = useRef(null);

  // Handle node click
  const onNodeClick = (event, node) => {
    event.stopPropagation();
    setSelectedNode(node);
    setNodeFormData({
      id: node.id,
      name: node.data.label,
      type: node.type,
      branches: node.data.branches || []
    });
    setShowNodeForm(true);
  };

  // Handle background click to close forms
  const onPaneClick = () => {
    setSelectedNode(null);
    setShowNodeForm(false);
    setShowNodeMenu(false);
  };

  // Handle edge click to show add node menu
  const onEdgeClick = (event, edge) => {
    event.preventDefault();
    setClickedEdgeId(edge.id);

    // Calculate position for the node menu
    const sourceNode = nodes.find(node => node.id === edge.source);
    const targetNode = nodes.find(node => node.id === edge.target);

    if (sourceNode && targetNode) {
      const x = (sourceNode.position.x + targetNode.position.x) / 2;
      const y = (sourceNode.position.y + targetNode.position.y) / 2;

      setMenuPosition({ x, y });
      setShowNodeMenu(true);
    }
  };

  // Add a new node between two existing nodes
  const addNodeBetween = (nodeType) => {
    if (!clickedEdgeId) return;

    const edge = edges.find(e => e.id === clickedEdgeId);
    if (!edge) return;

    const sourceNode = nodes.find(node => node.id === edge.source);
    const targetNode = nodes.find(node => node.id === edge.target);

    if (!sourceNode || !targetNode) return;

    // Calculate position for the new node
    const newNodePosition = {
      x: (sourceNode.position.x + targetNode.position.x) / 2,
      y: (sourceNode.position.y + targetNode.position.y) / 2,
    };

    const newNodeId = `${nodeType}-${nodeCount}`;

    let newNode = {
      id: newNodeId,
      type: nodeType,
      position: newNodePosition,
      data: { label: nodeType === 'action' ? 'Action Node' : 'If / Else' },
    };

    // If it's an if/else node, add default branches
    if (nodeType === 'ifelse') {
      newNode.data.branches = [
        { id: 'branch-1', name: 'Branch #1' },
        { id: 'branch-2', name: 'Branch #2' }
      ];

      // Create branch nodes
      const branchNodes = [
        {
          id: `${newNodeId}-branch-1-label`,
          type: 'branch',
          position: { 
            x: newNodePosition.x - 150, 
            y: newNodePosition.y + 100 
          },
          data: { label: 'Branch #1' },
        },
        {
          id: `${newNodeId}-branch-2-label`,
          type: 'branch',
          position: { 
            x: newNodePosition.x + 150, 
            y: newNodePosition.y + 100 
          },
          data: { label: 'Branch #2' },
        },
        {
          id: `${newNodeId}-else-label`,
          type: 'branch',
          position: { 
            x: newNodePosition.x, 
            y: newNodePosition.y + 100 
          },
          data: { label: 'Else' },
        }
      ];

      // Create action nodes for each branch (instead of end nodes)
      const actionNodes = [
        {
          id: `${newNodeId}-branch-1-action`,
          type: 'action',
          position: { 
            x: newNodePosition.x - 150, 
            y: newNodePosition.y + 200 
          },
          data: { label: 'Branch #1 Action' },
        },
        {
          id: `${newNodeId}-branch-2-action`,
          type: 'action',
          position: { 
            x: newNodePosition.x + 150, 
            y: newNodePosition.y + 200 
          },
          data: { label: 'Branch #2 Action' },
        },
        {
          id: `${newNodeId}-else-action`,
          type: 'action',
          position: { 
            x: newNodePosition.x, 
            y: newNodePosition.y + 200 
          },
          data: { label: 'Else Action' },
        }
      ];

      // Create a merge node to join all branches
      const mergeNode = {
        id: `${newNodeId}-merge`,
        type: 'action',
        position: { 
          x: newNodePosition.x, 
          y: newNodePosition.y + 300 
        },
        data: { label: 'Merge' },
      };

      // Create branch edges
      const branchEdges = [
        // Connect if/else to branch labels
        {
          id: `${newNodeId}-to-branch-1`,
          source: newNodeId,
          target: `${newNodeId}-branch-1-label`,
          type: 'smoothstep',
        },
        {
          id: `${newNodeId}-to-branch-2`,
          source: newNodeId,
          target: `${newNodeId}-branch-2-label`,
          type: 'smoothstep',
        },
        {
          id: `${newNodeId}-to-else`,
          source: newNodeId,
          target: `${newNodeId}-else-label`,
          type: 'smoothstep',
        },

        // Connect branch labels to actions
        {
          id: `${newNodeId}-branch-1-label-to-action`,
          source: `${newNodeId}-branch-1-label`,
          target: `${newNodeId}-branch-1-action`,
          type: 'smoothstep',
        },
        {
          id: `${newNodeId}-branch-2-label-to-action`,
          source: `${newNodeId}-branch-2-label`,
          target: `${newNodeId}-branch-2-action`,
          type: 'smoothstep',
        },
        {
          id: `${newNodeId}-else-label-to-action`,
          source: `${newNodeId}-else-label`,
          target: `${newNodeId}-else-action`,
          type: 'smoothstep',
        },

        // Connect actions to merge node
        {
          id: `${newNodeId}-branch-1-action-to-merge`,
          source: `${newNodeId}-branch-1-action`,
          target: `${newNodeId}-merge`,
          type: 'smoothstep',
        },
        {
          id: `${newNodeId}-branch-2-action-to-merge`,
          source: `${newNodeId}-branch-2-action`,
          target: `${newNodeId}-merge`,
          type: 'smoothstep',
        },
        {
          id: `${newNodeId}-else-action-to-merge`,
          source: `${newNodeId}-else-action`,
          target: `${newNodeId}-merge`,
          type: 'smoothstep',
        },

        // Connect merge node to target
        {
          id: `${newNodeId}-merge-to-target`,
          source: `${newNodeId}-merge`,
          target: edge.target,
          type: 'smoothstep',
        }
      ];

      // Add all nodes and edges
      setNodes(nds => [...nds, newNode, ...branchNodes, ...actionNodes, mergeNode]);
      setEdges(eds => {
        // Remove the original edge
        const filteredEdges = eds.filter(e => e.id !== clickedEdgeId);

        // Add new edges
        return [
          ...filteredEdges,
          {
            id: `${edge.source}-${newNodeId}`,
            source: edge.source,
            target: newNodeId,
            type: 'smoothstep',
          },
          ...branchEdges
        ];
      });
    } else {
      // For action nodes
      setNodes(nds => [...nds, newNode]);

      // Update edges
      setEdges(eds => {
        // Remove the original edge
        const filteredEdges = eds.filter(e => e.id !== clickedEdgeId);

        // Add new edges
        return [
          ...filteredEdges,
          {
            id: `${edge.source}-${newNodeId}`,
            source: edge.source,
            target: newNodeId,
            type: 'smoothstep',
          },
          {
            id: `${newNodeId}-${edge.target}`,
            source: newNodeId,
            target: edge.target,
            type: 'smoothstep',
          }
        ];
      });
    }

    setNodeCount(nodeCount + 1);
    setShowNodeMenu(false);
  };

  // Add a new action node directly from the Add Node button
  const addActionNode = () => {
    const newNodeId = `action-${nodeCount}`;

    // Find the last node before the end node
    let lastNodeBeforeEnd = 'start';
    let lastNodeY = 50; // Default Y position of start node

    // Find all edges that connect to the end node
    const endEdges = edges.filter(edge => edge.target === 'end');

    if (endEdges.length > 0) {
      // Get the source node of the edge connecting to end
      lastNodeBeforeEnd = endEdges[0].source;
      const lastNode = nodes.find(node => node.id === lastNodeBeforeEnd);
      if (lastNode) {
        lastNodeY = lastNode.position.y;
      }
    }

    // Calculate position for the new node
    const newNodePosition = {
      x: 300, // Keep X position consistent
      y: lastNodeY + 150, // Position below the last node
    };

    // Create the new node
    const newNode = {
      id: newNodeId,
      type: 'action',
      position: newNodePosition,
      data: { label: 'Action Node' },
    };

    // Add the new node
    setNodes(nds => [...nds, newNode]);

    // Remove the edge connecting to end
    setEdges(eds => {
      // Filter out the edge connecting to end
      const filteredEdges = eds.filter(edge => !(edge.source === lastNodeBeforeEnd && edge.target === 'end'));

      // Add new edges
      return [
        ...filteredEdges,
        {
          id: `${lastNodeBeforeEnd}-${newNodeId}`,
          source: lastNodeBeforeEnd,
          target: newNodeId,
          type: 'smoothstep',
        },
        {
          id: `${newNodeId}-end`,
          source: newNodeId,
          target: 'end',
          type: 'smoothstep',
        }
      ];
    });

    setNodeCount(nodeCount + 1);
  };

  // Handle form submission for node editing
  const handleNodeFormSubmit = (e) => {
    e.preventDefault();

    if (!selectedNode) return;

    // Update node data
    setNodes(nds => 
      nds.map(node => {
        if (node.id === selectedNode.id) {
          // For if/else nodes, update branch names too
          if (node.type === 'ifelse') {
            // Update branch label nodes
            const updatedNode = {
              ...node,
              data: {
                ...node.data,
                label: nodeFormData.name,
                branches: nodeFormData.branches
              }
            };
            return updatedNode;
          }

          // For other node types
          return {
            ...node,
            data: {
              ...node.data,
              label: nodeFormData.name
            }
          };
        } else if (selectedNode.type === 'ifelse' && node.id.includes('branch') && node.id.startsWith(selectedNode.id.split('-')[0])) {
          // Update branch label nodes
          const branchIndex = parseInt(node.id.split('-')[1]) - 1;
          if (branchIndex >= 0 && branchIndex < nodeFormData.branches.length) {
            return {
              ...node,
              data: {
                ...node.data,
                label: nodeFormData.branches[branchIndex].name
              }
            };
          }
        }
        return node;
      })
    );

    setShowNodeForm(false);
    setSelectedNode(null);
  };

  // Handle node deletion
  const deleteNode = () => {
    if (!selectedNode || selectedNode.id === 'start' || selectedNode.id === 'end') return;

    // Find incoming and outgoing edges
    const incomingEdges = edges.filter(edge => edge.target === selectedNode.id);
    const outgoingEdges = edges.filter(edge => edge.source === selectedNode.id);

    // For if/else nodes, we need to handle differently
    if (selectedNode.type === 'ifelse') {
      // Find all related branch nodes and their edges
      const relatedBranchNodes = nodes.filter(node => 
        node.id.includes('branch') && 
        (node.id.startsWith(selectedNode.id.split('-')[0]) || node.id.includes('else'))
      );

      const relatedBranchNodeIds = relatedBranchNodes.map(node => node.id);

      // Find all edges connected to branch nodes
      const branchEdges = edges.filter(edge => 
        relatedBranchNodeIds.includes(edge.source) || 
        relatedBranchNodeIds.includes(edge.target)
      );

      // Remove the if/else node, branch nodes, and all related edges
      setNodes(nds => nds.filter(node => 
        node.id !== selectedNode.id && 
        !relatedBranchNodeIds.includes(node.id)
      ));

      // Remove all edges connected to this node and branch nodes
      setEdges(eds => {
        const filteredEdges = eds.filter(edge => 
          edge.source !== selectedNode.id && 
          edge.target !== selectedNode.id &&
          !branchEdges.some(be => be.id === edge.id)
        );

        // Connect the incoming node to the end node
        if (incomingEdges.length > 0) {
          filteredEdges.push({
            id: `${incomingEdges[0].source}-end`,
            source: incomingEdges[0].source,
            target: 'end',
            type: 'smoothstep',
          });
        }

        return filteredEdges;
      });
    } else {
      // For regular action nodes
      // Remove just this node
      setNodes(nds => nds.filter(node => node.id !== selectedNode.id));

      // Remove edges connected to this node and reconnect
      setEdges(eds => {
        const filteredEdges = eds.filter(edge => 
          edge.source !== selectedNode.id && 
          edge.target !== selectedNode.id
        );

        // If there were both incoming and outgoing edges, connect them
        if (incomingEdges.length > 0 && outgoingEdges.length > 0) {
          filteredEdges.push({
            id: `${incomingEdges[0].source}-${outgoingEdges[0].target}`,
            source: incomingEdges[0].source,
            target: outgoingEdges[0].target,
            type: 'smoothstep',
          });
        }

        return filteredEdges;
      });
    }

    setShowNodeForm(false);
    setSelectedNode(null);
  };

  // Add a new branch to an if/else node
  const addBranch = () => {
    if (!selectedNode || selectedNode.type !== 'ifelse') return;

    const newBranchId = `branch-${nodeFormData.branches.length + 1}`;
    const newBranch = { id: newBranchId, name: `Branch #${nodeFormData.branches.length + 1}` };

    setNodeFormData({
      ...nodeFormData,
      branches: [...nodeFormData.branches, newBranch]
    });

    setBranchCount(branchCount + 1);
  };

  // Remove a branch from an if/else node
  const removeBranch = (branchId) => {
    if (!selectedNode || selectedNode.type !== 'ifelse') return;

    setNodeFormData({
      ...nodeFormData,
      branches: nodeFormData.branches.filter(branch => branch.id !== branchId)
    });
  };

  // Update branch name
  const updateBranchName = (branchId, newName) => {
    if (!selectedNode || selectedNode.type !== 'ifelse') return;

    setNodeFormData({
      ...nodeFormData,
      branches: nodeFormData.branches.map(branch => 
        branch.id === branchId ? { ...branch, name: newName } : branch
      )
    });
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }} ref={reactFlowWrapper}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onNodeClick={onNodeClick}
        onEdgeClick={onEdgeClick}
        onPaneClick={onPaneClick}
        nodeTypes={nodeTypes}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />

        <Panel position="top-left">
          <div style={{ display: 'flex', gap: '10px' }}>
            <button
              onClick={addActionNode}
              style={{
                padding: '10px',
                backgroundColor: '#1890ff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              Add Node
            </button>
          </div>
        </Panel>
      </ReactFlow>

      {/* Node Selection Menu */}
      {showNodeMenu && (
        <div
          style={{
            position: 'absolute',
            top: `${menuPosition.y}px`,
            left: `${menuPosition.x}px`,
            backgroundColor: 'white',
            padding: '10px',
            borderRadius: '5px',
            boxShadow: '0 0 10px rgba(0,0,0,0.2)',
            zIndex: 1000,
            transform: 'translate(-50%, -50%)',
          }}
        >
          <div style={{ marginBottom: '10px', fontWeight: 'bold' }}>Add Node:</div>
          <div style={{ display: 'flex', gap: '10px' }}>
            <button
              onClick={() => addNodeBetween('action')}
              style={{
                padding: '8px 12px',
                backgroundColor: '#1890ff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              Action Node
            </button>
            <button
              onClick={() => addNodeBetween('ifelse')}
              style={{
                padding: '8px 12px',
                backgroundColor: '#ffc53d',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              If / Else Node
            </button>
          </div>
        </div>
      )}

      {/* Node Edit Form */}
      {showNodeForm && selectedNode && (
        <div
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            backgroundColor: 'white',
            padding: '20px',
            borderRadius: '8px',
            boxShadow: '0 0 20px rgba(0,0,0,0.3)',
            zIndex: 1000,
            minWidth: '400px',
            maxWidth: '600px',
          }}
        >
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
            <h3 style={{ margin: 0 }}>
              {selectedNode.type === 'action' ? 'Action' : 
               selectedNode.type === 'ifelse' ? 'If / Else' : 
               selectedNode.type === 'start' ? 'Start Node' : 'End Node'}
            </h3>
            <button
              onClick={() => setShowNodeForm(false)}
              style={{
                background: 'none',
                border: 'none',
                fontSize: '18px',
                cursor: 'pointer',
              }}
            >
              ‚úï
            </button>
          </div>

          <form onSubmit={handleNodeFormSubmit}>
            <div style={{ marginBottom: '15px' }}>
              <label style={{ display: 'block', marginBottom: '5px', fontWeight: 'bold' }}>
                Action Name
              </label>
              <input
                type="text"
                value={nodeFormData.name}
                onChange={(e) => setNodeFormData({ ...nodeFormData, name: e.target.value })}
                style={{
                  width: '100%',
                  padding: '8px',
                  borderRadius: '4px',
                  border: '1px solid #d9d9d9',
                }}
              />
            </div>

            {selectedNode.type === 'ifelse' && (
              <div style={{ marginBottom: '15px' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
                  <label style={{ fontWeight: 'bold' }}>BRANCHES</label>
                  <button
                    type="button"
                    onClick={addBranch}
                    style={{
                      padding: '5px 10px',
                      backgroundColor: '#1890ff',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer',
                      fontSize: '12px',
                    }}
                  >
                    + Add branch
                  </button>
                </div>

                {nodeFormData.branches.map((branch, index) => (
                  <div 
                    key={branch.id} 
                    style={{ 
                      marginBottom: '10px',
                      padding: '10px',
                      borderRadius: '4px',
                      border: '1px solid #f0f0f0',
                      backgroundColor: '#fafafa',
                    }}
                  >
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                      <input
                        type="text"
                        value={branch.name}
                        onChange={(e) => updateBranchName(branch.id, e.target.value)}
                        style={{
                          width: '80%',
                          padding: '8px',
                          borderRadius: '4px',
                          border: '1px solid #d9d9d9',
                        }}
                      />
                      <button
                        type="button"
                        onClick={() => removeBranch(branch.id)}
                        style={{
                          background: 'none',
                          border: 'none',
                          color: '#ff4d4f',
                          cursor: 'pointer',
                          fontSize: '16px',
                        }}
                      >
                        ‚úï
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}

            <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '20px' }}>
              {selectedNode.id !== 'start' && selectedNode.id !== 'end' && (
                <button
                  type="button"
                  onClick={deleteNode}
                  style={{
                    padding: '8px 16px',
                    backgroundColor: '#ff4d4f',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                  }}
                >
                  Delete
                </button>
              )}
              <div style={{ display: 'flex', gap: '10px' }}>
                <button
                  type="button"
                  onClick={() => setShowNodeForm(false)}
                  style={{
                    padding: '8px 16px',
                    backgroundColor: '#f0f0f0',
                    color: '#000',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                  }}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  style={{
                    padding: '8px 16px',
                    backgroundColor: '#1890ff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                  }}
                >
                  Save
                </button>
              </div>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}
