import React, { useState, useRef } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,

} from 'reactflow';
import 'reactflow/dist/style.css';

const nodeTypes = {
  start: ({ data }) => (
    <div style={nodeStyle('#e6f7ff', '#1890ff')}>
      <Handle type="source" position="bottom" style={handleStyle('#1890ff')} />
      <div style={nodeLabelStyle}>üöÄ Start Node</div>
      <div>{data.label || 'Start'}</div>
    </div>
  ),
  action: ({ data }) => (
    <div style={nodeStyle('#f0f5ff', '#597ef7')}>
      <Handle type="target" position="top" style={handleStyle('#597ef7')} />
      <Handle type="source" position="bottom" style={handleStyle('#597ef7')} />
      <div style={nodeLabelStyle}>‚öôÔ∏è Action Node</div>
      <div>{data.label || 'Action'}</div>
    </div>
  ),
  ifelse: ({ data }) => (
    <div style={nodeStyle('#fff7e6', '#ffc53d')}>
      <Handle type="target" position="top" style={handleStyle('#ffc53d')} />
      <Handle type="source" position="bottom" style={handleStyle('#ffc53d')} />
      <div style={nodeLabelStyle}>üîÄ If / Else</div>
      <div>{data.label || 'If / Else'}</div>
    </div>
  ),
  end: ({ data }) => (
    <div style={nodeStyle('#f9f0ff', '#9254de')}>
      <Handle type="target" position="top" style={handleStyle('#9254de')} />
      <div style={nodeLabelStyle}>üèÅ End Node</div>
      <div>{data.label || 'END'}</div>
    </div>
  ),
};

const initialNodes = [
  { id: 'start', type: 'start', position: { x: 300, y: 50 }, data: { label: 'Start' } },
  { id: 'end', type: 'end', position: { x: 300, y: 400 }, data: { label: 'END' } },
];

const initialEdges = [
  { id: 'start->end', source: 'start', target: 'end', type: 'smoothstep' },
];

export default function App() {
  const [nodes, setNodes] = useNodesState(initialNodes);
  const [edges, setEdges] = useEdgesState(initialEdges);
  const [selectedNode, setSelectedNode] = useState(null);
  const [showNodeForm, setShowNodeForm] = useState(false);
  const [nodeFormData, setNodeFormData] = useState({ name: '', type: '' });
  const reactFlowWrapper = useRef(null);

  const onNodeClick = (event, node) => {
    setSelectedNode(node);
    setNodeFormData({ id: node.id, name: node.data.label, type: node.type });
    setShowNodeForm(true);
  };

  const onPaneClick = () => {
    setSelectedNode(null);
    setShowNodeForm(false);
  };

  const handleNodeFormSubmit = (e) => {
    e.preventDefault();
    setNodes((nds) =>
      nds.map((node) =>
        node.id === selectedNode.id
          ? { ...node, data: { ...node.data, label: nodeFormData.name } }
          : node
      )
    );
    setShowNodeForm(false);
    setSelectedNode(null);
  };

  const deleteNode = () => {
    if (!selectedNode || selectedNode.id === 'start' || selectedNode.id === 'end') return;
    setNodes((nds) => nds.filter((node) => node.id !== selectedNode.id));
    setEdges((eds) => eds.filter((edge) => edge.source !== selectedNode.id && edge.target !== selectedNode.id));
    setShowNodeForm(false);
    setSelectedNode(null);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }} ref={reactFlowWrapper}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={setNodes}
        onEdgesChange={setEdges}
        onNodeClick={onNodeClick}
        onPaneClick={onPaneClick}
        nodeTypes={nodeTypes}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>

      {showNodeForm && selectedNode && (
        <div style={formStyle}>
          <h3>Edit Node</h3>
          <form onSubmit={handleNodeFormSubmit}>
            <div style={formGroupStyle}>
              <label>Node Name:</label>
              <input
                type="text"
                value={nodeFormData.name}
                onChange={(e) => setNodeFormData({ ...nodeFormData, name: e.target.value })}
                style={inputStyle}
              />
            </div>
            <div style={formActionsStyle}>
              <button type="submit" style={buttonStyle('#1890ff')}>Save</button>
              <button type="button" onClick={deleteNode} style={buttonStyle('#ff4d4f')}>Delete</button>
              <button type="button" onClick={() => setShowNodeForm(false)} style={buttonStyle('#d9d9d9')}>Cancel</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}

const nodeStyle = (bgColor, borderColor) => ({
  padding: '15px',
  borderRadius: '8px',
  background: bgColor,
  border: `1px solid ${borderColor}`,
  width: '180px',
  position: 'relative',
});

const handleStyle = (bgColor) => ({
  background: bgColor,
  width: '10px',
  height: '10px',
});

const nodeLabelStyle = {
  display: 'flex',
  alignItems: 'center',
  marginBottom: '8px',
  fontWeight: 'bold',
};

const formStyle = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  backgroundColor: 'white',
  padding: '20px',
  borderRadius: '5px',
  boxShadow: '0 0 10px rgba(0,0,0,0.2)',
  zIndex: 1000,
  transform: 'translate(-50%, -50%)',
  minWidth: '300px',
};

const formGroupStyle = {
  marginBottom: '15px',
};

const inputStyle = {
  width: '100%',
  padding: '8px',
  borderRadius: '4px',
  border: '1px solid #d9d9d9',
};

const formActionsStyle = {
  display: 'flex',
  justifyContent: 'space-between',
};

const buttonStyle = (bgColor) => ({
  padding: '10px',
  backgroundColor: bgColor,
  color: bgColor === '#d9d9d9' ? 'black' : 'white',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
});
