import React, { useState, useCallback } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  Position,
} from 'react-flow-renderer';
import 'react-flow-renderer/dist/style.css';

// Custom Action Node with form for name change
const ActionNode = ({ data, isConnectable }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [nodeName, setNodeName] = useState(data.label);

  const handleSubmit = (e) => {
    e.preventDefault();
    data.onChange(nodeName);
    setIsEditing(false);
  };

  return (
    <div style={{ padding: 10, background: '#f0f8ff', borderRadius: 5, border: '1px solid #4682b4' }}>
      {isEditing ? (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={nodeName}
            onChange={(e) => setNodeName(e.target.value)}
            autoFocus
          />
          <button type="submit">Save</button>
        </form>
      ) : (
        <>
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <div>
              <img src="data:image/svg+xml;base64," alt="Action" width="24" height="24" style={{ marginRight: 8 }} />
              <span>{data.label}</span>
            </div>
            <div>
              <button 
                onClick={() => setIsEditing(true)}
                style={{ marginRight: 5, background: 'none', border: 'none', cursor: 'pointer' }}
              >
                ‚úèÔ∏è
              </button>
              <button 
                onClick={data.onDelete}
                style={{ background: 'none', border: 'none', cursor: 'pointer' }}
              >
                üóëÔ∏è
              </button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

// Node types definition
const nodeTypes = {
  actionNode: ActionNode,
};

const initialNodes = [
  {
    id: 'start',
    position: { x: 250, y: 50 },
    data: { label: 'Start Node' },
    style: { background: '#d6f5d6', padding: 10, borderRadius: 5 },
    sourcePosition: Position.Bottom,
  },
  {
    id: 'end',
    position: { x: 250, y: 300 },
    data: { label: 'End' },
    style: { background: '#e0e0e0', padding: 10, borderRadius: 20, width: 100 },
    targetPosition: Position.Top,
  },
];

const initialEdges = [
  {
    id: 'e-initial',
    source: 'start',
    target: 'end',
    type: 'smoothstep',
  },
];

export default function App() {
  const [nodes, setNodes, onNodesChange] = useNodesState(initialNodes);
  const [edges, setEdges, onEdgesChange] = useEdgesState(initialEdges);
  const [nodeCount, setNodeCount] = useState(0);

  // Update node name
  const updateNodeName = useCallback((nodeId, newName) => {
    setNodes((nds) =>
      nds.map((node) => {
        if (node.id === nodeId) {
          return {
            ...node,
            data: {
              ...node.data,
              label: newName,
            },
          };
        }
        return node;
      })
    );
  }, [setNodes]);

  // Delete node and reconnect edges
  const deleteNode = useCallback((nodeId) => {
    // Find the node to delete
    const nodeToDelete = nodes.find(node => node.id === nodeId);
    if (!nodeToDelete) return;

    // Find incoming and outgoing edges
    const incomingEdges = edges.filter(edge => edge.target === nodeId);
    const outgoingEdges = edges.filter(edge => edge.source === nodeId);

    // Create new edges to connect nodes around the deleted node
    const newConnections = [];
    incomingEdges.forEach(incoming => {
      outgoingEdges.forEach(outgoing => {
        newConnections.push({
          id: `e-${incoming.source}-${outgoing.target}`,
          source: incoming.source,
          target: outgoing.target,
          type: 'smoothstep',
        });
      });
    });

    // Remove all edges connected to the node
    const filteredEdges = edges.filter(
      edge => edge.source !== nodeId && edge.target !== nodeId
    );

    // Set new edges with reconnections
    setEdges([...filteredEdges, ...newConnections]);

    // Remove the node
    setNodes(nodes.filter(node => node.id !== nodeId));
  }, [nodes, edges, setNodes, setEdges]);

  // Add new action node - Fixed version
  const addActionNode = () => {
    console.log("Adding action node"); // Debug log

    const nodeId = `actionNode-${nodeCount}`;

    // Calculate vertical positions for proper spacing
    const endNode = nodes.find(node => node.id === 'end');
    const startNode = nodes.find(node => node.id === 'start');
    const startY = startNode?.position.y || 50;
    const endY = endNode?.position.y || 300;

    // Count current action nodes
    const actionNodeCount = nodes.filter(node => node.id.includes('actionNode')).length;

    // Calculate position for new node
    let newNodeY;
    let updatedNodes = [...nodes];

    if (actionNodeCount === 0) {
      // First action node goes in the middle
      newNodeY = startY + (endY - startY) / 2;
    } else {
      // Space nodes evenly
      const totalSpacing = endY - startY;
      const step = totalSpacing / (actionNodeCount + 2);

      // Reposition existing action nodes for even spacing
      updatedNodes = nodes.map(node => {
        if (node.id === 'start' || node.id === 'end') {
          return node;
        }

        // Find the index of this action node
        const nodeIndex = parseInt(node.id.split('-')[1]) + 1;
        return {
          ...node,
          position: {
            x: node.position.x,
            y: startY + (step * nodeIndex)
          }
        };
      });

      // Position for new node
      newNodeY = startY + (step * (actionNodeCount + 1));
    }

    // Create new action node
    const newNode = {
      id: nodeId,
      type: 'actionNode',
      position: { x: 250, y: newNodeY },
      data: { 
        label: `Action Node ${nodeCount + 1}`,
        onChange: (newName) => updateNodeName(nodeId, newName),
        onDelete: () => deleteNode(nodeId)
      },
      sourcePosition: Position.Bottom,
      targetPosition: Position.Top,
    };

    // Update edges
    let newEdges = [];

    if (actionNodeCount === 0) {
      // First node - replace direct connection
      newEdges = edges.filter(edge => !(edge.source === 'start' && edge.target === 'end'));

      // Add connections through new node
      newEdges.push(
        {
          id: `e-start-${nodeId}`,
          source: 'start',
          target: nodeId,
          type: 'smoothstep',
        },
        {
          id: `e-${nodeId}-end`,
          source: nodeId,
          target: 'end',
          type: 'smoothstep',
        }
      );
    } else {
      // Find the last node in the chain
      const actionNodes = updatedNodes.filter(n => n.id.includes('actionNode'));
      actionNodes.sort((a, b) => a.position.y - b.position.y);

      const lastNode = actionNodes[actionNodes.length - 1];

      // Remove connection from last node to end
      newEdges = edges.filter(edge => !(edge.source === lastNode.id && edge.target === 'end'));

      // Keep all other connections
      edges.forEach(edge => {
        if (!(edge.source === lastNode.id && edge.target === 'end')) {
          newEdges.push(edge);
        }
      });

      // Add connections to and from new node
      newEdges.push(
        {
          id: `e-${lastNode.id}-${nodeId}`,
          source: lastNode.id,
          target: nodeId,
          type: 'smoothstep',
        },
        {
          id: `e-${nodeId}-end`,
          source: nodeId,
          target: 'end',
          type: 'smoothstep',
        }
      );
    }

    // Update state
    setNodes([...updatedNodes, newNode]);
    setEdges(newEdges);
    setNodeCount(nodeCount + 1);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        nodeTypes={nodeTypes}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />
      </ReactFlow>
      <button
        onClick={addActionNode}
        style={{
          position: 'absolute',
          top: 10,
          left: 10,
          padding: '10px 20px',
          background: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: 5,
          cursor: 'pointer',
        }}
      >
        Add Action Node
      </button>
    </div>
  );
}
