  },
  data: {
    label: 'Start'
  }
}, {
  id: 'end',
  type: 'end',
  position: {
    x: 300,
    y: 400
  },
  data: {
    label: 'END'
  }
}];
const initialEdges = [{
  id: 'start->end',
  source: 'start',
  target: 'end',
  type: 'smoothstep',
  sourceHandle: 'source',
  targetHandle: 'target'
}];
