    // Create action node for the new branch
    const branchActionNode = {
      id: `${selectedNode.id}-${newBranchId}-action`,
      type: 'action',
      position: branchActionPosition,
      data: {
        label: `${newBranch.name} Action`
      }
    };

    // Create end node for the new branch
    const branchEndNode = {
