import React, { useState, useRef } from 'react';
import ReactFlow, {
  MiniMap,
  Controls,
  Background,
  useNodesState,
  useEdgesState,
  Panel,
} from 'reactflow';
import 'reactflow/dist/style.css';
import { Handle, Position } from 'reactflow';
// Firstly, we import all the necessary libraries and components needed for the project to work



// Custom node types
//here we define and desine the nodes that will be used for the flow diagram
const nodeTypes = {
  start: ({ data, id }) => (
    <div style={{
      padding: '15px',
      borderRadius: '8px',
      background: '#e6f7ff',
      border: '1px solid #1890ff',
      width: '180px',
      position: 'relative',
    }}>
      <Handle
        type="source"
        position={Position.Bottom}
        id="source"
        style={{ background: '#1890ff', width: '10px', height: '10px' }}
      />
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
        <div style={{ 
          width: '0px', 
          height: '0px', 
          borderRadius: '50%', 
          background: '#e6f7ff', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          marginRight: '10px',
          border: '1px solid #1890ff'
        }}>

        </div>
        <div style={{ fontWeight: 'bold' }}>  Start Node</div>
      </div>
      <div>{data.label || 'Start'}</div>
    </div>
  ),
  action: ({ data, id }) => (
    <div style={{
      padding: '15px',
      borderRadius: '8px',
      background: '#f0f5ff',
      border: '1px solid #597ef7',
      width: '180px',
      position: 'relative',
    }}>
      <Handle
        type="target"
        position={Position.Top}
        id="target"
        style={{ background: '#597ef7', width: '10px', height: '10px' }}
      />
      <Handle
        type="source"
        position={Position.Bottom}
        id="source"
        style={{ background: '#597ef7', width: '10px', height: '10px' }}
      />
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: '8px' }}>
        <div style={{ 
          width: '30px', 
          height: '30px', 
          borderRadius: '50%', 
          background: '#f0f5ff', 
          display: 'flex', 
          alignItems: 'center', 
          justifyContent: 'center',
          marginRight: '10px',
          border: '1px solid #597ef7'
        }}>
          <span role="img" aria-label="action"></span>
        </div>
        <div style={{ fontWeight: 'bold' }}>Action Node</div>
