  // Handle edge click to show add node menu
  const onEdgeClick = (event, edge) => {
    setClickedEdgeId(edge.id);

    //positioning where node menu will appear
      setMenuPosition({ x: 150, y: 100 });
      setShowNodeMenu(true);

  };

  // Add a new node between two existing nodes
  const addNodeBetween = (nodeType) => {
    if (!clickedEdgeId) return; //check if an edge is clicked

    const edge = edges.find(e => e.id === clickedEdgeId);
    if (!edge) return; //retrieve the edge that was clicked 


    //get the source and target node of the once clicked
    const sourceNode = nodes.find(node => node.id === edge.source);
    const targetNode = nodes.find(node => node.id === edge.target);

    if (!sourceNode || !targetNode) return; //check if there is a source and target node existence

    // calculating position for the new node
    const newNodePosition = {
      x: (sourceNode.position.x + targetNode.position.x) / 2,
      y: (sourceNode.position.y + targetNode.position.y) / 2,
    };

    //generating a new node id
    const newNodeId = `${nodeType}-${nodeCount}`;


    //defing the new node
    let newNode = {
      id: newNodeId,
      type: nodeType,
      position: newNodePosition,
      data: { label: nodeType === 'action' ? 'Action Node' : 'If / Else' },
    };



    // If it's an if/else node, add default branches
    if (nodeType === 'ifelse') {
      newNode.data.branches = [
        { id: 'branch-1', name: 'Branch #1' }
      ];

      // Create action nodes for each branch
      const actionNodes = [
        {
          id: `${newNodeId}-branch-1-action`,
          type: 'action',
          position: { 
            x: newNodePosition.x - 150, 
            y: newNodePosition.y + 100 
          },
          data: { label: 'Branch #1 Action' },
        },
        {
          id: `${newNodeId}-else-action`,
          type: 'action',
          position: { 
            x: newNodePosition.x, 
            y: newNodePosition.y + 100 
          },
          data: { label: 'Else Action' },
        }
      ];

      // Create an end node for the else branch
      const elseEndNode = {
        id: `${newNodeId}-else-end`,
        type: 'end',
        position: { 
          x: newNodePosition.x, 
          y: newNodePosition.y + 300 
        },
        data: { label: 'END' },
      };

      // Create branch edges
      const branchEdges = [
        // Connect if/else directly to actions
        {
          id: `${newNodeId}-to-branch-1-action`,
          source: newNodeId,
          target: `${newNodeId}-branch-1-action`,
          type: 'smoothstep',
          sourceHandle: 'source',
          targetHandle: 'target',
        },
        {
          id: `${newNodeId}-to-else-action`,
          source: newNodeId,
          target: `${newNodeId}-else-action`,
          type: 'smoothstep',
          sourceHandle: 'source',
          targetHandle: 'target',
        },

        // Connect branch #1 action to target
        {
          id: `${newNodeId}-branch-1-action-to-target`,
          source: `${newNodeId}-branch-1-action`,
          target: edge.target,
          type: 'smoothstep',
          sourceHandle: 'source',
          targetHandle: 'target',
        },

        // Connect else action to its own end node
        {
          id: `${newNodeId}-else-action-to-end`,
          source: `${newNodeId}-else-action`,
          target: `${newNodeId}-else-end`,
          type: 'smoothstep',
          sourceHandle: 'source',
          targetHandle: 'target',
        }
      ];

      // Add all nodes and edges
      setNodes(nds => [...nds, newNode, ...actionNodes, elseEndNode]);
      setEdges(eds => {
        // Remove the original edge
        const filteredEdges = eds.filter(e => e.id !== clickedEdgeId);

        // Add new edges
        return [
          ...filteredEdges,
          {
            id: `${edge.source}-${newNodeId}`,
            source: edge.source,
            target: newNodeId,
            type: 'smoothstep',
            sourceHandle: 'source',
            targetHandle: 'target',
          },
          ...branchEdges
        ];
      });
    } else {
      // For action nodes
      setNodes(nds => [...nds, newNode]);

      // Update edges
      setEdges(eds => {
        // Remove the original edge
        const filteredEdges = eds.filter(e => e.id !== clickedEdgeId);

        // Add new edges
        return [
          ...filteredEdges,
          {
            id: `${edge.source}-${newNodeId}`,
            source: edge.source,
            target: newNodeId,
            type: 'smoothstep',
            sourceHandle: 'source',
            targetHandle: 'target',
          },
          {
            id: `${newNodeId}-${edge.target}`,
            source: newNodeId,
            target: edge.target,
            type: 'smoothstep',
            sourceHandle: 'source',
            targetHandle: 'target',
          }
        ];
      });
    }

    //update node count and close the node menu
    setNodeCount(nodeCount + 1);
    setShowNodeMenu(false);
  };

