        branch.id === branchId ? { ...branch, name: newName } : branch
      )
    });
  };

  // Handle form submission for node editing
  const handleNodeFormSubmit = (e) => {
    e.preventDefault();

    if (!selectedNode) return;

    // Get the current branches in the node
    const currentBranches = selectedNode.data.branches || [];
    // Get the updated branches from the form
    const updatedBranches = nodeFormData.branches;

    // Find branches that were removed
    const removedBranches = currentBranches.filter(
      currentBranch => !updatedBranches.some(
        updatedBranch => updatedBranch.id === currentBranch.id
      )
    );

    // Update node data
    setNodes(nds => 
      nds.map(node => {
        if (node.id === selectedNode.id) {
          // For if/else nodes, update branch names too
          if (node.type === 'ifelse') {
            // Update branch label nodes
            const updatedNode = {
              ...node,
              data: {
                ...node.data,
                label: nodeFormData.name,
                branches: nodeFormData.branches
              }
            };
            return updatedNode;
          }

          // For other node types
          return {
            ...node,
            data: {
              ...node.data,
              label: nodeFormData.name
            }
          };
        } else if (selectedNode.type === 'ifelse' && node.id.includes('branch') && node.id.startsWith(selectedNode.id.split('-')[0])) {
          // Update branch label nodes
          const branchIndex = parseInt(node.id.split('-')[1]) - 1;
          if (branchIndex >= 0 && branchIndex < nodeFormData.branches.length) {
            return {
              ...node,
              data: {
                ...node.data,
                label: nodeFormData.branches[branchIndex].name
              }
            };
          }
        }
        return node;
      })
    );

    // Make sure we don't create any automatic connections to the main end node
    // for action nodes whose end nodes were removed
    if (selectedNode.type === 'ifelse' && removedBranches.length > 0) {
      // Find any edges that might have been automatically created to connect
      // branch action nodes to the main end node
      const autoCreatedEdges = edges.filter(edge => 
        removedBranches.some(branch => 
          edge.source.includes(`${selectedNode.id}-${branch.id}-action`) && 
          edge.target === 'end'
        )
      );

      // Remove these automatically created edges
      if (autoCreatedEdges.length > 0) {
        setEdges(eds => eds.filter(edge => 
          !autoCreatedEdges.some(autoEdge => autoEdge.id === edge.id)
        ));
      }
    }

    setShowNodeForm(false);
    setSelectedNode(null);
  };

  // Handle node deletion
  const deleteNode = () => {
    if (!selectedNode || selectedNode.id === 'start' || selectedNode.id === 'end') return;

    // Find incoming and outgoing edges
    const incomingEdges = edges.filter(edge => edge.target === selectedNode.id);
    const outgoingEdges = edges.filter(edge => edge.source === selectedNode.id);

    // For if/else nodes, we need to handle differently
    if (selectedNode.type === 'ifelse') {
      // Find all related nodes (branch nodes, action nodes, end nodes)
      const relatedNodes = nodes.filter(node => 
        node.id.includes(selectedNode.id) || 
        (node.id.includes('branch') && node.id.includes(selectedNode.id.split('-')[1])) ||
        node.id.includes(`${selectedNode.id}-else-end`)
      );

      const relatedNodeIds = relatedNodes.map(node => node.id);

      // Find all edges connected to related nodes
      const relatedEdges = edges.filter(edge => 
        relatedNodeIds.includes(edge.source) || 
        relatedNodeIds.includes(edge.target) ||
        edge.source === selectedNode.id ||
        edge.target === selectedNode.id
      );

      // Remove the if/else node and all related nodes
      setNodes(nds => nds.filter(node => 
        node.id !== selectedNode.id && 
        !relatedNodeIds.includes(node.id)
      ));

      // Remove all edges connected to this node and related nodes
      setEdges(eds => {
        const filteredEdges = eds.filter(edge => 
          !relatedEdges.some(re => re.id === edge.id)
        );

        // Connect the incoming node to the next node or end
        if (incomingEdges.length > 0) {
          // Connect the incoming node directly to the end node
          filteredEdges.push({
            id: `${incomingEdges[0].source}-end`,
            source: incomingEdges[0].source,
            target: 'end',
            type: 'smoothstep',
            sourceHandle: 'source',
            targetHandle: 'target',
          });
        } else {
          // If no incoming edges (unlikely), ensure start is connected to end
          const hasStartToEndEdge = filteredEdges.some(edge => 
            edge.source === 'start' && edge.target === 'end'
          );

          if (!hasStartToEndEdge) {
            filteredEdges.push({
              id: 'start-end',
              source: 'start',
              target: 'end',
              type: 'smoothstep',
              sourceHandle: 'source',
              targetHandle: 'target',
            });
          }
        }

        return filteredEdges;
      });
    } else {
      // For regular action nodes
      // Remove just this node
      setNodes(nds => nds.filter(node => node.id !== selectedNode.id));

      // Remove edges connected to this node and reconnect
      setEdges(eds => {
        const filteredEdges = eds.filter(edge => 
          edge.source !== selectedNode.id && 
          edge.target !== selectedNode.id
        );

        // If there were both incoming and outgoing edges, connect them
        if (incomingEdges.length > 0 && outgoingEdges.length > 0) {
          filteredEdges.push({
            id: `${incomingEdges[0].source}-${outgoingEdges[0].target}`,
            source: incomingEdges[0].source,
            target: outgoingEdges[0].target,
            type: 'smoothstep',
            sourceHandle: 'source',
            targetHandle: 'target',
          });
        } else if (incomingEdges.length > 0) {
          // If only incoming edges, connect to end
          filteredEdges.push({
            id: `${incomingEdges[0].source}-end`,
            source: incomingEdges[0].source,
            target: 'end',
            type: 'smoothstep',
            sourceHandle: 'source',
            targetHandle: 'target',
          });
        } else if (outgoingEdges.length > 0) {
          // If only outgoing edges, connect start to the target
          filteredEdges.push({
            id: `start-${outgoingEdges[0].target}`,
            source: 'start',
            target: outgoingEdges[0].target,
            type: 'smoothstep',
            sourceHandle: 'source',
            targetHandle: 'target',
          });
        } else {
          // If no edges at all, ensure start is connected to end
          const hasStartToEndEdge = filteredEdges.some(edge => 
            edge.source === 'start' && edge.target === 'end'
          );

          if (!hasStartToEndEdge) {
            filteredEdges.push({
              id: 'start-end',
              source: 'start',
              target: 'end',
              type: 'smoothstep',
              sourceHandle: 'source',
              targetHandle: 'target',
            });
          }
        }

        return filteredEdges;
      });
    }

    setShowNodeForm(false);
    setSelectedNode(null);
  };

  return (
    <div style={{ width: '100vw', height: '100vh' }} ref={reactFlowWrapper}>
      <ReactFlow
        nodes={nodes}
        edges={edges}
        onNodesChange={onNodesChange}
        onEdgesChange={onEdgesChange}
        onNodeClick={onNodeClick}
        onEdgeClick={onEdgeClick}
        onPaneClick={onPaneClick}
        nodeTypes={nodeTypes}
        fitView
      >
        <Controls />
        <MiniMap />
        <Background variant="dots" gap={12} size={1} />

        <Panel position="top-left">
          <div style={{ display: 'flex', gap: '10px' }}>
            {/* <button
              onClick={showAddNodeMenuHandler}
              style={{
                padding: '10px',
                backgroundColor: '#1890ff',
                color: 'white',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
              }}
            >
              Add Node
            </button> */}
          </div>
        </Panel>
      </ReactFlow>

      {/* Node Selection Menu */}
      {showNodeMenu && (
        <div
          style={{
            position: 'absolute',
            top: `${menuPosition.y}px`,
            left: `${menuPosition.x}px`,
            backgroundColor: 'white',
            padding: '10px',
            borderRadius: '5px',
            boxShadow: '0 0 10px rgba(0,0,0,0.2)',
            zIndex: 1000,
            transform: 'translate(-50%, -50%)',
          }}
        >
          <div style={{ marginBottom: '10px', fontWeight: 'bold' }}>Add Node:</div>
          <div style={{ display: 'flex', gap: '10px' }}>
            <button
              onClick={() => addNodeBetween('action')}
              style={{
                padding: '8px 12px',
                backgroundColor: '#1890ff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              Action Node
            </button>
            <button
              onClick={() => addNodeBetween('ifelse')}
              style={{
                padding: '8px 12px',
                backgroundColor: '#ffc53d',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              If / Else Node
            </button>
          </div>
        </div>
      )}

      {/* Add Node Menu */}
      {showAddNodeMenu && (
        <div
          style={{
            position: 'absolute',
            top: `${menuPosition.y}px`,
            left: `${menuPosition.x}px`,
            backgroundColor: 'white',
            padding: '10px',
            borderRadius: '5px',
            boxShadow: '0 0 10px rgba(0,0,0,0.2)',
            zIndex: 1000,
            transform: 'translate(-50%, -50%)',
          }}
        >
          <div style={{ marginBottom: '10px', fontWeight: 'bold' }}>Add Node:</div>
          <div style={{ display: 'flex', gap: '10px' }}>
            {/* <button
              onClick={addActionNode}
              style={{
                padding: '8px 12px',
                backgroundColor: '#1890ff',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              Action Node
            </button> */}
            {/* <button
              onClick={addIfElseNode}
              style={{
                padding: '8px 12px',
                backgroundColor: '#ffc53d',
                color: 'white',
                border: 'none',
                borderRadius: '4px',
                cursor: 'pointer',
              }}
            >
              If / Else Node
            </button> */}
          </div>
        </div>
      )}

      {/* Node Form */}
      {showNodeForm && selectedNode && (
        <div
          style={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            backgroundColor: 'white',
            padding: '20px',
            borderRadius: '5px',
            boxShadow: '0 0 10px rgba(0,0,0,0.2)',
            zIndex: 1000,
            transform: 'translate(-50%, -50%)',
            minWidth: '300px',
          }}
        >
          <h3 style={{ marginTop: 0 }}>Edit Node</h3>
          <form onSubmit={handleNodeFormSubmit}>
            <div style={{ marginBottom: '15px' }}>
              <label style={{ display: 'block', marginBottom: '5px' }}>Node Name:</label>
              <input
                type="text"
                value={nodeFormData.name}
                onChange={(e) => setNodeFormData({ ...nodeFormData, name: e.target.value })}
                style={{
                  width: '100%',
                  padding: '8px',
                  borderRadius: '4px',
                  border: '1px solid #d9d9d9',
                }}
              />
            </div>

            {selectedNode.type === 'ifelse' && (
              <div style={{ marginBottom: '15px' }}>
                <label style={{ display: 'block', marginBottom: '5px' }}>Branches:</label>
                {nodeFormData.branches.map((branch, index) => (
                  <div key={branch.id} style={{ display: 'flex', marginBottom: '5px', alignItems: 'center' }}>
                    <input
                      type="text"
                      value={branch.name}
                      onChange={(e) => updateBranchName(branch.id, e.target.value)}
                      style={{
                        flex: 1,
                        padding: '8px',
                        borderRadius: '4px',
                        border: '1px solid #d9d9d9',
                        marginRight: '5px',
                      }}
                    />
                    {index > 0 && (
                      <button
                        type="button"
                        onClick={() => removeBranch(branch.id)}
                        style={{
                          padding: '8px',
                          backgroundColor: '#ff4d4f',
                          color: 'white',
                          border: 'none',
                          borderRadius: '4px',
                          cursor: 'pointer',
                        }}
                      >
                        Remove
                      </button>
                    )}
                  </div>
                ))}
                <button
                  type="button"
                  onClick={addBranch}
                  style={{
                    padding: '8px',
                    backgroundColor: '#52c41a',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    cursor: 'pointer',
                    marginTop: '5px',
                  }}
                >
                  Add Branch
                </button>
              </div>
            )}

            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <button
                type="submit"
                style={{
                  padding: '10px',
                  backgroundColor: '#1890ff',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                }}
              >
                Save
              </button>
              <button
                type="button"
                onClick={deleteNode}
                style={{
                  padding: '10px',
                  backgroundColor: '#ff4d4f',
                  color: 'white',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                }}
              >
                Delete Node
              </button>
              <button
                type="button"
                onClick={() => {
                  setShowNodeForm(false);
                  setSelectedNode(null);
                }}
                style={{
                  padding: '10px',
                  backgroundColor: '#d9d9d9',
                  color: 'black',
                  border: 'none',
                  borderRadius: '4px',
                  cursor: 'pointer',
                }}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}
